    private void backtrack() {
      List<WaypointNode> path = new ArrayList<WaypointNode>();
      path.add(goalNode);
      WaypointNode parent = goalNode;
      while (parent != null) {
        parent = parent.getParent();
        path.add(parent);
      }
    }
