```java
public static int sumArray(int[] arr) {
    int sum = 0;
    int i = 0;
    int len = arr.length;
    while (i < len - 1) {
    sum += arr[i++] + arr[i++];
    }
    if (i == len - 1) {
    sum += arr[i];
    }
    return sum;
}
